import React, { useEffect } from 'react'
import Profile from './Profile'
import { connect } from 'react-redux'
import { setProfileThunk } from '../../redux/profile-reducer'
import { withRouter } from 'react-router-dom'
import preloader from '../../assets/img/preloader.gif'
import s from './Profile.module.css'

const ProfileContainer = (props) => {
    useEffect(() => {
        let currentUser = props.match.params.id
        if (!currentUser) currentUser = props.myId
        props.setProfileThunk(currentUser)
    }, [props.myId])

    if (!props.isLoaded) return <img className={s.img} src={preloader} />
    return (
        <Profile {...props} />
    )
}

const mapStateToProps = (state) => ({
    profileInfo: state.profilePage.profileInfo,
    isLoaded: state.profilePage.isLoaded,
    myId: state.auth.id
})

const profileConteinerR = withRouter(ProfileContainer)
