import React, { useEffect } from 'react'
import Profile from './Profile'
import { connect } from 'react-redux'
import { setProfile, load } from '../../redux/profile-reducer'
import { getProfile } from '../api/api'
import { withRouter } from 'react-router-dom'
import preloader from '../../assets/img/preloader.'

const ProfileContainer = (props) => {
    useEffect(() => {
        let currentUser = props.match.params.id
        if (!currentUser)  currentUser = 2
        props.load(false)
        getProfile(currentUser).then(response => {
            props.load(true)
            props.setProfile(response.data)
        })
    }, [])

    if (!props.isLoaded) return <img src={preloader} />
    return (
        <Profile {...props}/>
    )
}

const mapStateToProps = (state) => ({
    profileInfo: state.profilePage.profileInfo,
    isLoaded: state.profilePage.isLoaded
})
const profileConteinerR = withRouter(ProfileContainer)

export default connect(mapStateToProps, {setProfile, load})(profileConteinerR)
