import React from 'react'
import s from './Users.module.css'
import { NavLink } from 'react-router-dom'
import { followUser, unfollowUser } from '../api/api'

const Users = (props) => {
    const pageCountNum = Math.ceil(props.usersCount / props.trueUsersCount)
    let pageCount = []
    for (let i = 1; i <= pageCountNum; i++) {
        pageCount.push(i)
    }
    return (
        <div className={s.main}>
            {pageCount.map(item => <span className={props.activePage === item ? s.active : s.page} onClick={() => props.changePage(item)}>{` ${item}`}</span>)}
            {props.users.map(item => <User name={item.name} id={item.id} user={item}
            follow={props.follow} unfollow={props.unfollow} isFollowed={props.isFollowed} />)}
        </div>
    )
}

const User = (props) => {
    return (<>
        <NavLink to={`/Profile/${props.id}`} ><div>{props.name}</div></NavLink>
        {props.user.followed ?
            <button onClick={() => followUser(props.id).then(response => {
                if (response.data.resultCode === 0) props.unfollow(props.id)
            })}>unfollow</button>
            : <button onClick={() => unfollowUser(props.id).then(response => {if (response.data.resultCode === 0) props.follow(props.id)})}>follow</button>
        }

    </>
    )
}
export default Users
