const GET_AUTH = 'GET_AUTH'

const initialState = {
    myId: null,
    login: '',
    email: '',
    isAuth: false
}

const authReducer = (state=initialState, action) => {
    switch (action.type) {
        case GET_AUTH:
            return {...state, ...action.authInfo}
        default: return state
    }
}

export const getAuth = (...authInfo) => ({
