const GET_USERS = 'GET_USERS'

const initialState = {
    users: []
}
const usersReducer = (state=initialState, action) => {
    switch (action.type){
    case GET_USERS:
        return {...state, users: {...action.users}}
    }
}

export const setUsers = (users) => ({
    type: GET_USERS,
    users
})

export default usersReducer
