const GET_AUTH = 'GET_AUTH'
const LOGOUT = 'LOGOUT'

const initialState = {
    id: null,
    login: '',
    email: '',
    isAuth: false
}

const authReducer = (state=initialState, action) => {
    switch (action.type) {
        case GET_AUTH:
            return {...state, ...action.authInfo, isAuth: true}
        case LOGOUT: 
            return {}
        default: return state
    }
}

export const getAuth = (authInfo) => ({
    type: GET_AUTH,
    authInfo
})

export const logout = () => ({
    type: LOGOUT
})

export default authReducer
