const GET_PROFILE = 'GET_PROFILE'

const initialState = {
    profileInfo: {}
}

const profileReducer = (state=initialState, action) => {
    switch (action.type){
        case GET_PROFILE:
            return {...state, profileInfo: action.profileInfo}
        default: 
    }
}

export const setProfile = (profileInfo) => ({
    type: GET_PROFILE,
    profileInfo
})

export default profileReducer
