import React, { useEffect } from 'react'
import Users from './Users'
import { setUsers, changePage } from '../../redux/users-reducer'
import { connect } from 'react-redux'
import { getUsers } from '../api/api'

const UsersContainer = (props) => {
    useEffect(() => {
        getUsers(props.trueUsersCount, props.activePage).then(response => {
            props.setUsers(response.data.items, response.data.totalCount)})
    }, [props.activePage])
    return (
        <Users {...props}/>
    )
}

const mapStateToProps = (state) => ({
    users: state.usersPage.users,
    trueUsersCount: state.usersPage.trueUsersCount,
    activePage: state.usersPage.activePage,
    usersCount: state.usersPage.usersCount,
    isLoaded: state.user
})


export default connect(mapStateToProps, {setUsers, changePage})(UsersContainer)
