import React, { useEffect } from 'react'
import Header from './Header'
import { connect } from 'react-redux'
import { logoutThunk, getAuthThunk } from '../../redux/auth-reducer'
import { getAuthInfo } from '../api/api'

const HeaderContainer = (props) => {
    useEffect(() => {
        props.getAuthThunk
    }, [props.id])

    return (
        <Header isAuth={props.isAuth} logoutThunk={props.logoutThunk}/>
    )
}

const mapStateToProps = (state) => ({
    id: state.auth.id,
    isAuth: state.auth.isAuth
})


export default connect(mapStateToProps, { getAuthThunk, logoutThunk })(HeaderContainer)
