import React from 'react'
import s from './Users.module.css'
import { NavLink } from 'react-router-dom'
import { followUser, unfollowUser } from '../api/api'

const Users = (props) => {
    const pageCountNum = Math.ceil(props.usersCount / props.trueUsersCount)
    let pageCount = []
    for (let i = 1; i <= pageCountNum; i++) {
        pageCount.push(i)
    }
    return (
        <div className={s.main}>
            <div className={s.pages}>
                {pageCount.map(item => <span className={props.activePage === item ? s.active : s.page} onClick={() => props.changePage(item)}>{` ${item}`}</span>)}
            </div>
            {props.users.map(item => <User name={item.name} id={item.id} user={item}
                followThunk={props.followThunk} unfollow={props.unfollowThunk}
                disable={props.disable} enable={props.enable} isDisabled={props.isDisabled} />)}
        </div>
    )
}

const User = (props) => {
    return (<div className={s.user}>
        <NavLink className={s.link} to={`/Profile/${props.id}`} ><div>{props.name}</div></NavLink>
        {props.user.followed ?
            <button className={s.unfollow} disabled={props.isDisabled.some(item => item === props.id)} onClick={
                () => {
                    props.disable(props.id)
                    unfollowUser(props.id).then(response => {
                        if (response.data.resultCode === 0) {
                            props.unfollow(props.id)
                            props.enable(props.id)
                        }
                    })
                }}>unfollow</button>
            : <button className={s.follow} disabled={props.isDisabled.some(item => item === props.id)} onClick={
                () => {
                    props.disable(props.id)
                    followUser(props.id).then(response => {
                        if (response.data.resultCode === 0) {
                            props.follow(props.id)
                            props.enable(props.id)
                        }
                    })
                }
            }>follow</button>
        }

    </div>
    )
}
export default Users
