const GET_USERS = 'GET_USERS'
const CHANGE_PAGE = 'CHANGE_PAGE'
const LOAD = 'LOAD'
const FOLLOW = 'FOLLOW'
const UNFOLLOW = 'UNFOLLOW'

let initialState = {
    users: [],
    usersCount: 0,
    trueUsersCount: 100,
    activePage: 1,
    isLoaded: false,
    isFollowed: []
}

const usersReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_USERS:
            return { ...state, users: [...action.users], usersCount: action.usersCount }
        case CHANGE_PAGE:
            return { ...state, activePage: action.activePage }
        case LOAD:
            return { ...state, isLoaded: action.isLoaded }
        case FOLLOW:
            return { ...state, isFollowed: [...state.isFollowed, action.userId] }
        case FOLLOW:
            return { ...state, isFollowed: [...state.isFollowed, action.userId] }
        default: return state
    }
}

export const setUsers = (users, usersCount) => ({
    type: GET_USERS,
    users,
    usersCount
})
export const changePage = (activePage) => ({
    type: CHANGE_PAGE,
    activePage
})
export const load = (isLoaded) => ({
    type: LOAD,
    isLoaded
})
export const follow = (isFollowed) => ({
    type: FOLLOW,
    isFollowed
})


export default usersReducer
