import React, { useEffect } from 'react'
import Users from './Users'
import { setUsers, changePage, load } from '../../redux/users-reducer'
import { connect } from 'react-redux'
import { getUsers } from '../api/api'
import preloader from '../../assets/img/preloader.gif'
import s from'./Users.module.css'

const UsersContainer = (props) => {
    useEffect(() => {
        props.load(false)
        getUsers(props.trueUsersCount, props.activePage).then(response => {
            props.load(true)
            props.setUsers(response.data.items, response.data.totalCount)})
    }, [props.activePage])
    if (props.isLoaded === false) return <img className={s.img} src={preloader}/>
    return (
        <Users {...props}/>
    )
}

const mapStateToProps = (state) => ({
    users: state.usersPage.users,
    trueUsersCount: state.usersPage.trueUsersCount,
    activePage: state.usersPage.activePage,
    usersCount: state.usersPage.usersCount,
    isLoaded: state.usersPage.isLoaded
})


export default connect(mapStateToProps, {setUsers, changePage, load})(UsersContainer)
